--
-- Create procedure `Proc_FixedAssetImport_GetAll`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_FixedAssetImport_GetAll ()
COMMENT 'Procedure lấy ra danh sách các trường được import thông qua file excel'
BEGIN
  SELECT
    `if`.field_import_id,
    `if`.field_name,
    `if`.field_key,
    `if`.field_type,
    `if`.created_by,
    `if`.created_date,
    `if`.modified_by,
    `if`.modified_date
  FROM fixed_asset_import `if`
  ORDER BY created_date ASC;
END
$$

--
-- Create procedure `Proc_FixedAsset_GetByPaging`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_FixedAsset_GetByPaging (IN ms_offset int, IN ms_limit int, IN ms_where varchar(500))
COMMENT 'Procedure lấy ra danh sách tài sản theo lọc, phân trang và bao gồm summary cho danh sách tài sản'
BEGIN
  -- @ms_offset: số trang
  -- @ms_limit: số bản ghi trên 1 trang
  -- @ms_where: điều kiện lọc
  -- kiểm tra ms_where có trông || null hay không
  IF COALESCE(ms_where, '') = '' THEN
    SET ms_where = '1=1';
  END IF;
  -- set query lay ra list fixed asset bao gom paging va filter
  SET @filterQuery = CONCAT(
  ' SELECT fa.fixed_asset_id,
       fa.fixed_asset_code,
       fa.fixed_asset_name,
       fa.department_id,
       fa.department_code,
       fa.department_name,
       fa.fixed_asset_category_id,
       fa.fixed_asset_category_code,
       fa.fixed_asset_category_name,
       fa.cost,
       fa.quantity,
       fa.depreciation_rate,
       fa.purchase_date,
       fa.tracked_year,
       fa.life_time,
       fa.production_year,
       fa.start_using_date,
       fa.depreciation_year
    FROM fixed_asset fa
    WHERE ',
  ms_where, ' ORDER BY fa.created_date DESC LIMIT ',
  ms_offset, ' , ',
  ms_limit
  );
  PREPARE filterQuery FROM @filterQuery;
  EXECUTE filterQuery;
  DEALLOCATE PREPARE filterQuery;

  -- lay ra tong so record bao gom filter
  SET @filterCount = CONCAT(
  ' SELECT COUNT(1) as TotalRecord , SUM(fa.quantity) AS TotalQuantity, SUM(fa.cost) AS TotalOfCost
    FROM fixed_asset fa
    WHERE ',
  ms_where);
  PREPARE filterCount FROM @filterCount;
  EXECUTE filterCount;
  DEALLOCATE PREPARE filterCount;

END
$$

