--
-- Create trigger `trgUpdateFixedAssetCode`
--
CREATE
DEFINER = 'root'@'localhost'
TRIGGER trgUpdateFixedAssetCode
AFTER INSERT
ON fixed_asset
FOR EACH ROW
BEGIN
  DECLARE newCode varchar(255);
  DECLARE prefix varchar(255);
  DECLARE suffix varchar(255);
  DECLARE tempValue bigint;
  DECLARE lengthOfValue bigint;
  SET @oldCode = NEW.fixed_asset_code;
  -- AAA099BBB
  SET
  -- tempValue = 
  @tempValue = COALESCE(REGEXP_SUBSTR(@oldCode, '[0-9]+(?=[^0-9]*$)'), 0), -- regex tìm ra chuỗi chứa các số 0-9 mà đi sau k có số nào khác
  @lengthOfValue = COALESCE(LENGTH(@tempValue), 0), -- lay ra do dai cua chuoi 
  -- thực hiện tìm ra vị trí hiện tại
  @curIndex = LENGTH(@oldCode) - LENGTH(
  SUBSTRING(
  REGEXP_SUBSTR(@oldCode, '[0-9]+[^0-9]*$')
  , 1)
  )
  + 1,
  -- lấy ra phần tiền tố bằng cách lấy sub string từ vị trí đầu tới current index - 1
  @prefix = COALESCE(SUBSTRING(@oldCode, 1, @curIndex - 1), ''), -- lay ra phan dang truoc so
  -- lấy ra phần hậu tố bằng cách lấu sub string từ vị trí current index + (tổng kí tự phần số nếu có) tới độ dài cuối
  @suffix = COALESCE(SUBSTRING(@oldCode, @curIndex + COALESCE(@lengthOfValue, 0), LENGTH(@oldCode)), @oldCode);

  UPDATE coderule c
  SET c.prefix = COALESCE(@prefix, @newCode),
      c.suffix = COALESCE(@suffix, ''),
      c.Value = COALESCE(@tempValue, 0),
      c.lengthOfValue = COALESCE(@lengthOfValue, 0)
  WHERE c.CategoryCode = 'TSCD';
END
$$

